var expect = require('chai').expect;
var server = require('./fixture/wsfed-server');
var request = require('request');
var cheerio = require('cheerio');
const xpath = require('xpath');
const DOMParser = require('xmldom').DOMParser;

describe('wsfed', function () {
  before(function (done) {
    server.start(done);
  });

  after(function (done) {
    server.close(done);
  });

  describe('Validations', () => {
    it('returns 400 if we have more than one Assertion element', (done) => {
      request.get({
        jar: request.jar(),
        uri: 'http://localhost:5050/login?wa=wsignin1.0&wtrealm=urn:fixture-test'
      }, function (err, response, b){
        if(err) return done(err);
        expect(response.statusCode)
          .to.equal(200);


        const $ = cheerio.load(b);
        const wresult = $('input[name="wresult"]').attr('value');
        const wa = $('input[name="wa"]').attr('value');

        const root = new DOMParser().parseFromString(wresult);
        const assertion = xpath.select("//*[local-name(.)='Assertion']", root)[0];

        const copiedAssertion = assertion.cloneNode(true);
        assertion.appendChild(copiedAssertion);

        const modifiedResult = root.toString();

        request.post({
          jar: request.jar(),
          uri: 'http://localhost:5050/callback',
          form: { wresult: modifiedResult, wa: wa }
        }, function (err, response, _) {
          if (err) return done(err);
          expect(response.statusCode).to.equal(400);
          done();
        });
      });
    })

  })

  describe('normal flow', function () {
    var user, r, bod, $;

    before(function (done) {
      request.get({
        jar: request.jar(),
        uri: 'http://localhost:5050/login?wa=wsignin1.0&wtrealm=urn:fixture-test'
      }, function (err, response, b){
        if(err) return done(err);
        expect(response.statusCode)
          .to.equal(200);


        $ = cheerio.load(b);
        var wresult = $('input[name="wresult"]').attr('value');
        var wa = $('input[name="wa"]').attr('value');

        request.post({
          jar: request.jar(),
          uri: 'http://localhost:5050/callback',
          form: { wresult: wresult, wa: wa }
        }, function(err, response, body) {
          if(err) return done(err);

          r = response;
          bod = body;
          done();
        });
      });
    });

    it('should be valid signature', function(){
      expect(r.statusCode)
            .to.equal(200);
    });

    it('should return a valid user', function(){
      var user = JSON.parse(bod);
      expect(user['http://schemas.xmlsoap.org/ws/2005/05/identity/claims/nameidentifier'])
          .to.equal(server.fakeUser.id);
      expect(user['http://schemas.xmlsoap.org/ws/2005/05/identity/claims/emailaddress'])
          .to.equal(server.fakeUser.emails[0].value);
    });

  });

  describe('wresult without RequestedSecurityToken', function () {
    var user, r, bod, $;

    before(function (done) {
      request.post({
        jar: request.jar(),
        uri: 'http://localhost:5050/callback',
        form: { wresult: '<t:RequestSecurityTokenResponse xmlns:t="http://schemas.xmlsoap.org/ws/2005/02/trust"></t:RequestSecurityTokenResponse>' }
      }, function(err, response, body) {
        if(err) return done(err);
        r = response;
        bod = body;
        done();
      });
    });

    it('should return a 400', function(){
      expect(r.statusCode)
            .to.equal(400);
    });
  });

  describe('missing wresult in POST', function () {
    var user, r, bod, $;

    before(function (done) {
      request.post({
        jar: request.jar(),
        uri: 'http://localhost:5050/callback'
      }, function(err, response, body) {
        if(err) return done(err);
        r = response;
        bod = body;
        done();
      });
    });

    it('should redirect to idp', function(){
      expect(r.statusCode)
            .to.equal(302);
    });
  });

  describe('invalid wresult in POST', function () {
    var user, r, bod, $;

    before(function (done) {
      request.post({
        jar: request.jar(),
        uri: 'http://localhost:5050/callback',
        form: { wresult: 'foo' }
      }, function(err, response, body) {
        if(err) return done(err);
        r = response;
        bod = body;
        done();
      });
    });

    it('should return a 400', function(){
      expect(r.statusCode)
            .to.equal(400);
    });
  });

  describe('invalid wresult (xml) in POST', function () {
    var user, r, bod, $;

    before(function (done) {
      request.post({
        jar: request.jar(),
        uri: 'http://localhost:5050/callback/wresult-with-invalid-xml',
        form: { wresult: '<t:RequestSecurityTokenResponse Context="undefined" xmlns:t="http://schemas.xmlsoap.org/ws/2005/02/trust"><t:RequestedSecurityToken></saml:Assertion xmlns:saml="urn:oasis:names:tc:SAML:1.0:assertion" MajorVersion="1" MinorVersion="1" AssertionID="_dpEiwydz8xWGlw4HbWMg1XfOUdEpdaMC" IssueInstant="2017-05-24T17:52:42.498Z" Issuer="urn:fixture-test"><saml:Conditions NotBefore="2017-05-24T17:52:42.498Z" NotOnOrAfter="2017-05-25T01:52:42.498Z"><saml:AudienceRestrictionCondition><saml:Audience>urn:fixture-test</saml:Audience></saml:AudienceRestrictionCondition></saml:Conditions><saml:AttributeStatement><saml:Subject><saml:NameIdentifier Format="urn:oasis:names:tc:SAML:1.1:nameid-format:unspecified">12345678</saml:NameIdentifier><saml:SubjectConfirmation><saml:ConfirmationMethod>urn:oasis:names:tc:SAML:1.0:cm:bearer</saml:ConfirmationMethod></saml:SubjectConfirmation></saml:Subject><saml:Attribute AttributeNamespace="http://schemas.xmlsoap.org/ws/2005/05/identity/claims" AttributeName="nameidentifier"><saml:AttributeValue>12345678</saml:AttributeValue></saml:Attribute><saml:Attribute AttributeNamespace="http://schemas.xmlsoap.org/ws/2005/05/identity/claims" AttributeName="emailaddress"><saml:AttributeValue>jfoo@gmail.com</saml:AttributeValue></saml:Attribute><saml:Attribute AttributeNamespace="http://schemas.xmlsoap.org/ws/2005/05/identity/claims" AttributeName="name"><saml:AttributeValue>John Foo</saml:AttributeValue></saml:Attribute><saml:Attribute AttributeNamespace="http://schemas.xmlsoap.org/ws/2005/05/identity/claims" AttributeName="givenname"><saml:AttributeValue>John</saml:AttributeValue></saml:Attribute><saml:Attribute AttributeNamespace="http://schemas.xmlsoap.org/ws/2005/05/identity/claims" AttributeName="surname"><saml:AttributeValue>Foo</saml:AttributeValue></saml:Attribute></saml:AttributeStatement><saml:AuthenticationStatement AuthenticationMethod="urn:oasis:names:tc:SAML:1.0:am:password" AuthenticationInstant="2017-05-25T01:52:42.498Z"><saml:Subject><saml:NameIdentifier Format="urn:oasis:names:tc:SAML:1.1:nameid-format:unspecified">12345678</saml:NameIdentifier><saml:SubjectConfirmation><saml:ConfirmationMethod>urn:oasis:names:tc:SAML:1.0:cm:bearer</saml:ConfirmationMethod></saml:SubjectConfirmation></saml:Subject></saml:AuthenticationStatement><Signature xmlns="http://www.w3.org/2000/09/xmldsig#"><SignedInfo><CanonicalizationMethod Algorithm="http://www.w3.org/2001/10/xml-exc-c14n#"/><SignatureMethod Algorithm="http://www.w3.org/2001/04/xmldsig-more#rsa-sha256"/><Reference URI="#_dpEiwydz8xWGlw4HbWMg1XfOUdEpdaMC"><Transforms><Transform Algorithm="http://www.w3.org/2000/09/xmldsig#enveloped-signature"/><Transform Algorithm="http://www.w3.org/2001/10/xml-exc-c14n#"/></Transforms><DigestMethod Algorithm="http://www.w3.org/2001/04/xmlenc#sha256"/><DigestValue>fxxeOWyp3M3cVfglUEg0Fc0mVAm7QVCyrSX2Kflq8VE=</DigestValue></Reference></SignedInfo><SignatureValue></SignatureValue><KeyInfo><X509Data><X509Certificate></X509Certificate></X509Data></KeyInfo></Signature></saml:Assertion></t:RequestedSecurityToken></t:RequestSecurityTokenResponse>' }
      },
      function(err, response, body) {
        if(err) return done(err);
        r = response;
        bod = body;
        done();
      });
    });

    it('should return a 400', function(){
      expect(r.statusCode)
            .to.equal(400);
    });

    it('should be recognized as an invalid xml', function(){
      var err = JSON.parse(bod);
      expect(err.message)
          .to.equal('wresult should be a valid xml');;
    });
  });
});
